    public function dws_montage_sanitize($input)
    {
        $sanitary_values = array();
        if (isset($input['s3_access_key_0'])) {
            $sanitary_values['s3_access_key_0'] = sanitize_text_field($input['s3_access_key_0']);
        }

        if (isset($input['s3_secret_1'])) {
            $sanitary_values['s3_secret_1'] = sanitize_text_field($input['s3_secret_1']);
        }

        if (isset($input['s3_bucket_2'])) {
            $sanitary_values['s3_bucket_2'] = sanitize_text_field($input['s3_bucket_2']);
        }

        if (isset($input['s3_region_3'])) {
            $sanitary_values['s3_region_3'] = sanitize_text_field($input['s3_region_3']);
        }

        if (isset($input['jelenlegi_id_0'])) {
            $sanitary_values['jelenlegi_id_0'] = sanitize_text_field($input['jelenlegi_id_0']);
        }

        if (isset($input['kategoria_id_k_1'])) {
            $sanitary_values['kategoria_id_k_1'] = sanitize_text_field($input['kategoria_id_k_1']);
        }

        if (isset($input['filetype'])) {
            $valid_filetypes = ['png', 'jpeg', 'webp'];
            if (in_array($input['filetype'], $valid_filetypes)) {
                $sanitary_values['filetype'] = sanitize_text_field($input['filetype']);
            }
        }

        if (isset($input['quality'])) {
            $quality = intval($input['quality']);
            if ($quality >= 1 && $quality <= 100) {
                $sanitary_values['quality'] = $quality;
            }
        }

        return $sanitary_values;
    }

